$(document).ready(function(){var postIds=[];$(".main .article").each(function(){postIds.push($(this).attr("id").replaceAll("_","/"))});var threads=postIds.join(",");$.ajax({type:"GET",url:"http://api.duoshuo.com/threads/counts.jsonp",data:{short_name:$("#duoshuoId").text(),threads:threads},dataType:"jsonp",success:function(data){if(data.code!=0){return}for(var responseId in data.response){$($("#"+responseId.replaceAll("/","_")).find(".info-comment a")).text("("+data.response[responseId].comments+")");$($("#"+responseId.replaceAll("/","_")).find(".info-like a")).text("("+data.response[responseId].likes+")")}},error:function(data){console.log("fail: "+data)}})});String.prototype.replaceAll=function(s1,s2){var demo=this;while(demo.indexOf(s1)!=-1){demo=demo.replace(s1,s2)}return demo};