$(document).ready(function(){$(document.links).filter(function(){return this.hostname!=window.location.hostname}).attr("target","_blank");$(".main .article img").click(function(){window.open($(this).attr("src"));return false});var $sidebar=$(".aside"),$window=$(window),$document=$(document),offset=$sidebar.offset(),height=$document.height()-$window.height(),topPadding=43;var $backToTop=$("#back-to-top");$backToTop.click(function(){$("body,html").animate({scrollTop:0},1000);return false});$window.scroll(function(){if($window.scrollTop()>100){$backToTop.fadeIn(1500)}else{$backToTop.fadeOut(1000)}if($window.scrollTop()>=height){return}if($window.scrollTop()>offset.top){$sidebar.stop().animate({marginTop:$window.scrollTop()-offset.top+topPadding})}else{$sidebar.stop().animate({marginTop:26})}})});